    for (int j = i + 1; j < arr.Length; j++)
    {
        if (arr[j] < arr[minIndex])
            minIndex = j;
    }

    // –º–µ–Ω—è–µ–º –º–µ—Å—Ç–∞–º–∏ —Ç–µ–∫—É—â–∏–π —Å —Å–∞–º—ã–º –º–∞–ª–µ–Ω—å–∫–∏–º
    int temp = arr[i];
